INSERT INTO `recommend` VALUES ('0677762eadfe44d4a601798ff01eb312', '2004', '终极预测4套卷', '已经得到广大考生的认可，是考研前期人手一册的“押宝试题”', '15573073905151557307159(1).png', '本书严格按照《2019年全国硕士研究生入学统一考试思想政治理论考试大纲》的要求编写，包括4套试卷及参考答案。以质取胜，含金量高。把握了命题方向，而且每年真题中分析题的知识点基本都涵盖。本书是作者多年经验积淀之作，命题认真，经过十年实践检验', '国家开放大学出版社', '肖秀荣', '2019-05-08', 32.00);
INSERT INTO `recommend` VALUES ('0ed92336dc9148959f66bec43a9aa919', '2001', '毙考题', '每天在食堂吃饭，刷政治题。合理利用碎片化时间。', '15570949809721557094942(1).png', '每天在食堂吃饭，刷政治题。合理利用碎片化时间。同时可以看到每道题的正确率和评论。', '', '', '2019-05-06', NULL);
