INSERT INTO `recommend` VALUES ('29a3be62ed4c471c96050a16fc216391', '2001', '墨墨背单词', '背单词', '15573055708221557304987(1).png', '抗遗忘英语背单词软件，界面优雅、操作简洁、量身定制的记忆规划、高效的抗遗忘策略。', '', '', '2019-05-08', NULL);
