INSERT INTO `recommend` VALUES ('3be6f471a0e249dca4932fbd0ef56e8a', '2002', '人民日报', '每天早上大概六点十分会推送一个“新闻早班车”，', '15570951517221557095097(1).png', '每天早上大概六点十分会推送一个“新闻早班车”，简单介绍前一天新闻，政治考的就是这一年的重大新闻，为政治考试做准备。', '', '', '2019-05-06', NULL);
